from rest_framework import serializersfrom profile_api import modelsclass HelloSerializer(serializers.Serializer):    """Hello Serializer"""    name = serializers.CharField(max_length=55, min_length=4)class UserProfileSerializer(serializers.HyperlinkedModelSerializer):    class Meta:        model = models.UserProfile        fields = ['name', 'email', 'password', 'url']        extra_kwargs = {            'password': {                'write_only': True,                'style': {                    'input_type': 'password',                }            }        }    def create(self, validated_data):        """Create and return new user"""        user = models.UserProfile.objects.create_user(            email=validated_data['email'],            name=validated_data['name'],            password=validated_data['password']        )        return user    def update(self, instance, validated_data):        """update user profile data by setting password text to hash"""        if 'password' in validated_data:            password = validated_data.pop('password')            instance.set_password(password)        return super().update(instance, validated_data)